function verifyDate(filter, element) {
    switch (filter.name) {
        case 'dateBefore':
            if (filter.search > element.integerRegisterDate) return true;
            break;
        case 'dateAfter':
            if (filter.search < element.integerRegisterDate) return true;
            break;
        case 'dateBetween':
            if (filter.search[0] <= element.integerRegisterDate && element.integerRegisterDate <= filter.search[1]) return true;
            break;
    };
    return false;
};

